import React from 'react';
function PaymentGateway(props) {
    var total=0;
    props.cart.map(prd=>total+=parseInt(prd.price)*parseInt(prd.quantity))
    const Submitted=(e)=>{
     let obj={cardno:e.target.cardno.value,expdate:e.target.expdate.value,holdername:e.target.name.value,amount:total}
  props.add(obj)
        e.preventDefault();
    }
    return ( 
        <>
        <div className="navBody col flexAIC flexJCC w100">
            <h3 clasName="m3">Pay for total Amount {total}</h3>
            <div className="col w20 dbg2 fclWhite bRd5 p3">
                <form onSubmit={Submitted} className='col'>
                <label htmlFor="">Card Holder Name</label>
                <input type="text" name="name" required/>
                <label htmlFor="">Card No.</label>
                <input type="number" name="cardno" id="" required/>
                <label htmlFor="">Expiray date (write same)</label>
                <input type="text" name="expdate" required/>
                <button className="btn btn-info" type='submit'>Pay</button>
                </form>
            </div>
        </div>
        </>
     );
}

export default PaymentGateway;
